var menuButton=document.querySelector("#db-menu-button"),menu=document.querySelector("#db-site-menu"),nav=document.querySelector("#db-site-navigation");if(menu&&menuButton){menuHide();menuButton.addEventListener("click",menuToggle,false)}function menuToggle(e){if(e.target.getAttribute("aria-expanded")=="false"){menuShow()}else{menuHide()}}function menuShow(){menu.setAttribute("aria-hidden","false");menuButton.setAttribute("aria-expanded","true")}function menuHide(){menu.setAttribute("aria-hidden","true");menuButton.setAttribute("aria-expanded","false")}function triggerMenuHide(e){if(nav&&!nav.contains(e.target)){menuHide()}}document.addEventListener("focusin",function(e){triggerMenuHide(e)},true);document.addEventListener("click",function(e){triggerMenuHide(e)},true);